package fr.cora.mesindicateurssi.formatter;

import static org.apache.commons.lang.StringUtils.isBlank;

import java.text.ParseException;
import java.util.Locale;

import fr.cora.mesindicateurssi.domain.Legacy;
import fr.cora.mesindicateurssi.repository.LegacyRepository;
import fr.cora.mesindicateurssi.formatter.support.DiscoverableFormatter;
import fr.cora.mesindicateurssi.domain.LegacyPk;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

/**
 * {@link Formatter} for {@link Legacy} that is picked by automatically by {@link CustomFormattingConversionServiceFactory}
 *
 * @see Formatter
 * @see CustomFormattingConversionServiceFactory
 */
@Component
public class LegacyFormatter implements DiscoverableFormatter<Legacy> {

    @Autowired
    private LegacyRepository legacyRepository;

    @Override
    public Class<Legacy> getTarget() {
        return Legacy.class;
    }

    @Override
    public String print(Legacy legacy, Locale locale) {
        if (legacy == null) {
            return "";
        }

        StringBuilder sb = new StringBuilder();
        // make a nice display string here...

        if (legacy.getExtraInfo() != null) {
            sb.append(legacy.getExtraInfo()).append(" ");
        }

        if (sb.length() > 0) {
            return sb.toString();
        } else if (legacy.isNew()) {
            return legacy.getId().toString();
        } else {
            return legacy.toString();
        }
    }

    @Override
    public Legacy parse(String text, Locale locale) throws ParseException {
        if (isBlank(text)) {
            return new Legacy();
        }
        Legacy legacy = legacyRepository.findOne(LegacyPk.fromString(text));
        return legacy != null ? legacy : new Legacy();
    }
}
