package fr.cora.mesindicateurssi.formatter;

import static org.apache.commons.lang.StringUtils.isBlank;

import java.text.ParseException;
import java.util.Locale;

import fr.cora.mesindicateurssi.domain.Role;
import fr.cora.mesindicateurssi.repository.RoleRepository;
import fr.cora.mesindicateurssi.formatter.support.DiscoverableFormatter;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

/**
 * {@link Formatter} for {@link Role} that is picked by automatically by {@link CustomFormattingConversionServiceFactory}
 *
 * @see Formatter
 * @see CustomFormattingConversionServiceFactory
 */
@Component
public class RoleFormatter implements DiscoverableFormatter<Role> {

    @Autowired
    private RoleRepository roleRepository;

    @Override
    public Class<Role> getTarget() {
        return Role.class;
    }

    @Override
    public String print(Role role, Locale locale) {
        if (role == null) {
            return "";
        }

        StringBuilder sb = new StringBuilder();
        // make a nice display string here...

        if (role.getRoleName() != null) {
            sb.append(role.getRoleName()).append(" ");
        }

        if (sb.length() > 0) {
            return sb.toString();
        } else if (role.isNew()) {
            return role.getId().toString();
        } else {
            return role.toString();
        }
    }

    @Override
    public Role parse(String text, Locale locale) throws ParseException {
        if (isBlank(text)) {
            return new Role();
        }
        Role role = roleRepository.findOne(new Integer(text));
        return role != null ? role : new Role();
    }
}
